import { useState, useCallback, useRef, useEffect } from 'react';
import axios from 'axios';

const useHttpClient = () => {
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState();
  const [resData, setResData] = useState();

  const activeHttpRequests = useRef([]);

  const sendRequest = useCallback(
    async (url, method = 'GET', body = null, headers = {}) => {
      setLoading(true);

      const httpAbortCtrl = new AbortController();
      activeHttpRequests.current.push(httpAbortCtrl);

      await axios(url, { method, body, headers, signal: httpAbortCtrl.signal })
        .then(successfulResponse)
        .catch(error);

      function successfulResponse(response) {
        setLoading(false);
        const responseData = response.data.users;
        activeHttpRequests.current = activeHttpRequests.current.filter(
          (reqCtrl) => reqCtrl !== httpAbortCtrl,
        );
        setResData(responseData);
      }

      function error(error) {
        setLoading(false);
        setErrorMessage(
          error.response.data.message + ' ' + error.response.status ||
            'Something went wrong, please try again.',
        );
        console.log(
          error.response.data.message,
          error.response.status || 'Something went wrong, please try again.',
        );
      }
    },
    [],
  );

  const errorHandler = () => {
    setErrorMessage(null);
  };

  useEffect(() => {
    return () => {
      // eslint-disable-next-line react-hooks/exhaustive-deps
      activeHttpRequests.current.forEach((abortCtrl) => abortCtrl.abort());
    };
  }, []);

  return { sendRequest, errorHandler, loading, errorMessage, resData };
};

export default useHttpClient;
