import React, { useState, useEffect } from 'react';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import axios from 'axios';

import UsersList from '../components/UsersList';

// const USERS = [
//   {
//     id: 'u2',
//     name: 'Max Schwarz',
//     image: 'https://picsum.photos/500/300',
//     placeCount: 0,
//   },
//   {
//     id: 'u1',
//     name: 'Max Schwarz',
//     image: 'https://picsum.photos/501/300',
//     placeCount: 5,
//   },
//   {
//     id: 'u1',
//     name: 'Max Schwarz',
//     image: 'https://picsum.photos/500/301',
//     placeCount: 1,
//   },
//   {
//     id: 'u3',
//     name: 'Max Schwarz',
//     image: 'https://picsum.photos/500/299',
//     placeCount: 4,
//   },
// ];

const Users = () => {
  const [loading, setLoading] = useState(true);
  const [USERS, setUSERS] = useState([]);
  const [errorMessage, setErrorMessage] = useState();

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/users')
      .then(successfulResponse)
      .catch(error);

    function successfulResponse(response) {
      setLoading(false);
      const loadedUsers = [];
      const users = response.data.users;
      for (const key in users) {
        loadedUsers.push({
          id: users[key]._id,
          name: users[key].name,
          image: users[key].imageUrl,
          placeCount: 1,
        });
      }
      setUSERS(loadedUsers);
    }

    function error(error) {
      setLoading(false);
      setErrorMessage(
        error.response.data.message + ' ' + error.response.status ||
          'Something went wrong, please try again.',
      );
      console.log(
        error.response.data.message,
        error.response.status || 'Something went wrong, please try again.',
      );
    }
  }, []);
  const errorHandler = () => {
    setErrorMessage(null);
  };

  return (
    <React.Fragment>
      <ErrorModal error={errorMessage} onClear={errorHandler} />
      {loading && <LoadingSpinner asOverlay/>}
      <UsersList items={USERS} />
    </React.Fragment>
  );
};

export default Users;
