import React from 'react';
import styles from './Avatar.module.css';

const Avatar = props => {
  return (

    <div className={styles.avatar && `${props.className}`} style={props.style}>
      <img
        src={props.image}
        alt={props.alt}
        style={{ width: props.width, height: props.width }}
      />
    </div>
  );
};

export default Avatar;
