import React, { useState } from 'react';

import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
} from '../../shared/util/validators';
import Button from '../../shared/components/FormElements/Button';
import Input from '../../shared/components/FormElements/Input';
import { useForm } from '../../shared/hook/form-hook';

const DUMMY_DATAS = [
  {
    email: 'test@test.com',
    password: 'test',
  },
  {
    email: 'deneme@deneme.com',
    password: 'deneme',
  },
];

const Auth = () => {
  const [formState, inputHandler] = useForm(
    {
        email: {
          value: '',
          isValid: false,
        },
        password: {
          value: '',
          isValid: false,
        },
      },
      false,
  );

  const loginData = DUMMY_DATAS.find((user) => user.email === 'ibrahim');

  const formSubmitHandler = (event) => {
    event.preventDefault();
    console.log('Loging...');
    console.log(formState.inputs);
  };

  return (
    <form className='place-form' onSubmit={formSubmitHandler}>
      <Input
        id='email'
        element='input'
        type='Email'
        label='Email'
        validators={[VALIDATOR_EMAIL()]}
        errorText='Please enter a valid Email.'
        onInput={inputHandler}
        value={formState.email.value}
        valid={formState.email.isValid}
      />
      <Input
        onInput={inputHandler}
        id='password'
        element='input'
        type='password'
        label='Password'
        validators={[VALIDATOR_MINLENGTH(8)]}
        errorText='Please enter a valid password (min. 8 characters).'
        value={formState.password.value}
        valid={formState.password.isValid}
      />

      <React.Fragment>
        <Button to='/' inverse>
          CANCEL
        </Button>
        <Button onClick={formSubmitHandler} danger disabled={!formState.isValid}>
          Login
        </Button>
      </React.Fragment>
    </form>
  );
};
export default Auth;
