import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
} from '../../shared/util/validators';
import Button from '../../shared/components/FormElements/Button';
import Users from './Users';
import Input from '../../shared/components/FormElements/Input';
import { useForm } from '../../shared/hook/form-hook';

const DUMMY_DATAS = [
  {
    email: 'test@test.com',
    password: 'test',
  },
  {
    email: 'deneme@deneme.com',
    password: 'deneme',
  },
];

const Auth = () => {
  const [formState, inputHandler] = useForm(
    {
      title: {
        value: '',
        isValid: false,
      },
      description: {
        value: '',
        isValid: false,
      },
    },
    false,
  );

  const loginData = DUMMY_DATAS.find((user) => user.email === 'ibrahim');

  const updateSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  const cancelHandler = () => {
    return <Users />;
  };

  const loginHandler = () => {
    console.log('Loging...');
  };

  return (
    <form className='place-form' onSubmit={updateSubmitHandler}>
      <Input
        id='email'
        element='input'
        type='Email'
        label='Email'
        validators={[VALIDATOR_EMAIL()]}
        errorText='Please enter a valid Email.'
        onInput={inputHandler}
        value={formState.inputs.title.value}
        valid={formState.inputs.title.isValid}
      />
      <Input
        onInput={inputHandler}
        id='password'
        element='input'
        type='password'
        label='Password'
        validators={[VALIDATOR_MINLENGTH(8)]}
        errorText='Please enter a valid password (min. 8 characters).'
        value={formState.inputs.description.value}
        valid={formState.inputs.description.isValid}
      />

      <React.Fragment>
        <Button onClick={cancelHandler} inverse>
          CANCEL
        </Button>
        <Button onClick={loginHandler} danger disabled={!formState.isValid}>
          Login
        </Button>
      </React.Fragment>
    </form>
  );
};
export default Auth;
