import React, { useState, useEffect } from 'react';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import axios from 'axios';

import UsersList from '../components/UsersList';

const Users = () => {
  const [loading, setLoading] = useState(true);
  const [USERS, setUSERS] = useState([]);
  const [errorMessage, setErrorMessage] = useState();


  useEffect(() => {
    let loadedUsers = [];
    const sendRequest = async () => {
      await axios
        .get('http://localhost:5000/api/users')
        .then(successfulResponse)
        .catch(error);

      function successfulResponse(response) {
        setLoading(false);
        loadedUsers = response.data.users;
        setUSERS(loadedUsers); 
      };

      function error(error) {
        setLoading(false);
        setErrorMessage(
          error.response.data.message + ' ' + error.response.status ||
            'Something went wrong, please try again.',
        );
        console.log(
          error.response.data.message,
          error.response.status || 'Something went wrong, please try again.',
        );
      }
    };
    sendRequest();
  }, []);
  const errorHandler = () => {
    setErrorMessage(null);
  };

  return (
    <React.Fragment>
      <ErrorModal error={errorMessage} onClear={errorHandler} />
      {loading && <LoadingSpinner asOverlay />}
      {!loading && USERS && <UsersList items={USERS} />}
    </React.Fragment>
  );
};

export default Users;


for (const key in loadedUsers) {
  loadedUsers.push({
    id: loadedUsers[key]._id,
    name: loadedUsers[key].name,
    image: loadedUsers[key].imageUrl,
    placeCount: loadedUsers[key].places.length,
  });
}
