import React, { useReducer } from 'react';

import './Input.css';

const inputReducer = (state, action) => {
  switch (action.type) {
    case 'CHANGE':
      return {
        ...state,
        value: action.value,
        isValid: action.isValid,
      };
    default:
      return state;
  }
};

const Input = (props) => 
{
  const [inputState, dispatch] = useReducer(inputReducer, {
    value: props.value || '',
    isValid: props.isValid || false,
  });

  const inputHandler = (event) => { 
    dispatch({
        type: 'CHANGE',
        value: event.target.value,
        isValid: true,
        });
    };

  const element =
    props.element === 'input' ? (
      <input id={props.id} type={props.type} placeholder={props.placeholder} onChange={inputHandler} value={inputState.value}/>
    ) : (
      <textarea id={props.id} rows={props.rows || 3} onChange={inputHandler} value={inputState.value}/>
    );

  return (
    <div
      className={`form-control ${
        !inputState.isValid && 'form-control--invalid'
      }`}
    >
      <label htmlFor={props.id}>{props.label}</label>
      {element}
      {!inputState.isValid || inputState.value.lenght === 0 || <p>{props.errorText}</p>}
    </div>

  );
};

export default Input;
