import React, { useEffect, useState } from 'react';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import { useHttpClient } from '../../shared/hook/http-hook';

import UsersList from '../components/UsersList';

const Users = () => {
  const { sendRequest, errorHandler, loading, errorMessage, resData } =
    useHttpClient();
  const [USERS, setUSERS] = useState();

  useEffect(() => {
    sendRequest('http://localhost:5000/api/users');
  }, [sendRequest]);

  const loadedUsers = resData.users;
  console.log(loadedUsers);

  setUSERS(loadedUsers);

  return (
    <React.Fragment>
      <ErrorModal error={errorMessage} onClear={errorHandler} />
      {loading && <LoadingSpinner asOverlay />}
      {!loading && resData && <UsersList items={USERS} />}
    </React.Fragment>
  );
};

export default Users;
