import React, { useEffect } from 'react';
import axios from 'axios';

import './Map.css';

const Map = (props) => {
  var map = new window.google.maps.Map(document.getElementById('map-canvas'), {
    center: {
      lat: 12.9715987,
      lng: 77.59456269999998,
    },
    zoom: 12,
  });

  var searchBox = new window.google.maps.places.SearchBox(
    document.getElementById('pac-input'),
  );
  map.controls[window.google.maps.ControlPosition.TOP_CENTER].push(
    document.getElementById('pac-input'),
  );
  window.google.maps.event.addListener(
    searchBox,
    'places_changed',
    function () {
      searchBox.set('map', null);

      var places = searchBox.getPlaces();

      var bounds = new window.google.maps.LatLngBounds();
      var i, place;
      for (i = 0; (place = places[i]); i++) {
        (function (place) {
          var marker = new window.google.maps.Marker({
            position: place.geometry.location,
          });
          marker.bindTo('map', searchBox, 'map');
          window.google.maps.event.addListener(
            marker,
            'map_changed',
            function () {
              if (!this.getMap()) {
                this.unbindAll();
              }
            },
          );
          bounds.extend(place.geometry.location);
        })(place);
      }
      map.fitBounds(bounds);
      searchBox.set('map', map);
      map.setZoom(Math.min(map.getZoom(), 12));
    },
  );

  const mapRef = React.useRef();
  const API_KEY = 'AIzaSyDqTdWndNmtA1K5qd-phcMkZEgzRtZWY6U';

  useEffect(() => {
    axios
      .get(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURI(
          props.address,
        )}&key=${API_KEY}`,
      )
      .then((res) => {
        // res is the response from the server
        if (res.data.status !== 'OK') {
          throw new Error('No results found');
        }
        const location = res.data.results[0]?.geometry.location;

        const map = new window.google.maps.Map(mapRef.current, {
          center: location,
          //center: props.center, ---- null
          zoom: props.zoom,
        });

        new window.google.maps.Marker({
          position: location,
          // position: props.center,   ---- null
          map: map,
        });
        console.log(location);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [props.address, props.center, props.zoom]);

  return (
    <div ref={mapRef} className={`map ${props.className}`} style={props.style}>
      {props.children}
    </div>
  );
};

export default Map;
